어제 이성기씨 께서 요청한 custom hash table source입니다.
사용법은 main()를 참조.
일반 hashtable과는 다를게, 자동 sort를 하지 않음.

package ks.collection;

import java.util.Vector;
import java.io.*;

public class KTable
{
       protected Vector[] _table;
       protected int      _index = -1;

       public KTable()
       {
              _init(2,10);
       }

       public KTable(int columnSize)
       {
              _init(columnSize,10);
       }

       public KTable(int columnSize,int rowSize)
       {
             _init(columnSize,rowSize);
       }

       protected void _init(int columnSize,int rowSize)
       {
              _table = new Vector[columnSize];
              for ( int i = 0; i < columnSize; i++ )
              {
                    _table[i] = new Vector(rowSize);
              }
       }

       public KTable(InputStream input) throws IOException
       {
              _init(2,16);

              int size = input.available();
              byte[] buffer = new byte[size];
              input.read(buffer);

              input.close();

              while( true )
              {
                     String key = _getWord((byte)'=',buffer);
                     if ( key == null )
                     {
                          _index = -1;
                          return;
                     }

                     String value = _getWord((byte)0x0a,buffer);


                     add(key,value);
              }
       }



       public void add(String key, Object value)
       {
              _table[0].add(key);
              _table[1].add(value);
       }


       public void add(int column, Object value) throws Exception
       {
              _checkColumn(column,"add(col,value)");
              _table[column].add(value);
       }

       public void add(Object[] objects) throws Exception
       {
              _checkColumn( objects.length-1,"add(Object[])" );
              for ( int i = 0; i < objects.length ; i++ )
              {
                    _table[i].add(objects[i]);
              }
       }

       public Object get(int column, int row) throws Exception
       {
              _checkColumn(column,"get(col,row)");
              return _table[column].get(row);
       }

       public String getString(int column, int row) throws Exception
       {
              _checkColumn(column,"getString(col,row");
              return (String)_table[column].get(row);
       }


       public boolean hasMore()
       {
              if ( ++_index >= _table[0].size() )
              {
                   _index = -1;
                   return false;
              }
              else
              {
                   return true;
              }
       }

       public Object get(int column) throws Exception
       {
              _checkColumn(column,"get(col");
              if ( _index < 0 )
              {
                   throw new Exception("initialize first using KTable.hasMore()");
              }

              return _table[column].get(_index);
       }

       public String getString(int column) throws Exception
       {
              return (String) get(column);
       }

       public int size()
       {
              return _table[0].size();
       }

       public int indexOf(Object value)
       {
              return _table[0].indexOf(value);
       }

       public int indexOf(int column,Object value) throws Exception
       {
              _checkColumn(column ,"indexOf(col,value");
              return _table[column].indexOf(value);
       }

       public boolean search(Object value)
       {
              return _searching(0,value);
       }

       public boolean search(int column,Object value) throws Exception
       {
              _checkColumn(column,"search(col,value");
              return _searching(column,value);
       }

       protected boolean _searching(int column,Object value)
       {

              while ( ++_index < _table[column].size() )
              {
                      if ( _table[column].get(_index).equals(value) == true )
                      {
                           return true;
                      }
              }

              return false;
       }


       public Object getValue(String key)
       {
              return _getValue(key,1);
       }

       public Object getValue(String key, int column) throws Exception
       {
              _checkColumn(column,"getValue(key,col");
              return _getValue(key,column);
       }

       protected Object _getValue(String key, int column)
       {
              int index = indexOf(key);

              if ( index >= 0 )
              {
                   return _table[column].get(index);
              }
              else
              {
                   return null;
              }
       }

       public String getStringValue(String key)
       {
              return (String) _getValue(key,1);
       }

       public String getStringValue(String key, int column) throws Exception
       {
              return (String) getValue(key,column);
       }


       protected void _checkColumn(int column,String func) throws Exception
       {
              if ( column >= _table.length )
              {
                   throw new Exception( func + ": column size(" + column + ") is out of range(" + _table.length + ")!");
              }
       }

       private String _getWord(byte endChar,byte[] buffer)
       {
               if ( ++_index >= buffer.length )
               {
                    return null;
               }

               while(true)
               {
                      byte chr = buffer[_index];

                      while( chr <= ' ' )
                      {
                             if ( ++_index >= buffer.length )
                             {
                                  return null;
                             }

                             chr = buffer[_index];
                      }

                      if ( chr == '#' )
                      {
                           while( buffer[_index] != 0x0a )
                           {

                             if ( ++_index >= buffer.length )
                             {
                                  return null;
                             }
                           }

                           if ( ++_index >= buffer.length )
                           {
                                  return null;
                           }

                      }
                      else
                      {
                           break;
                      }
               }

               int start = _index;
               int end   = 0;

               while(_index < buffer.length)
               {
                     byte chr = buffer[_index];

                     if ( chr == endChar )
                     {
                          if ( end == 0 ) end = _index;


                          return new String(buffer,start, end-start );
                     }
                     else
                     if (  chr <= ' ' )
                     {
                           end = _index;
                     }

                     _index++;
               }

               return null;
       }



       public String toString()
       {
              int size = _table[0].size();
              StringBuffer sb = new StringBuffer("col = " + _table.length + " , row = " + size );

              sb.append("\r\n");

              for ( int row = 0; row < size ; row++ )
              {
                    for ( int col = 0; col < _table.length ; col++ )
                    {
                           sb.append( _table[col].get(row) );
                           sb.append("/" );
                    }
                    sb.append("\r\n");
              }

              return sb.toString();
       }

       public static void main(String[] args)
       {
              try
              {
                     KTable table = new KTable();

                     table.add("ON","Ontario");
                     table.add("QC","Quebec");
                     table.add("BC","British Columbia");
                     table.add("MB","Manitoba");

                     while( table.hasMore() )
                     {
                            System.out.print( table.get(0) + " = " );
                            System.out.println( table.get(1) );
                     }

                     System.out.println( "what is MB?  answer is = " + table.getValue("MB") );
              }
              catch(Exception exp)
              {
                    System.out.println(exp);
              }


              try
              {

                     final int  CODE  = 0;
                     final int  NAME  = 1;
                     final int  GRADE = 2;

                     KTable table = new KTable(3);

                     table.add(CODE, "a001");
                     table.add(NAME, "Andrew Sheen");
                     table.add(GRADE,"A+");

                     table.add(CODE, "a002");
                     table.add(NAME, "Sharon Stone");
                     table.add(GRADE,"C+");

                     table.add(CODE, "a003");
                     table.add(NAME, "Jeff back");
                     table.add(GRADE,"A+");

                     table.add(CODE, "a004");
                     table.add(NAME, "Jimmy Page");
                     table.add(GRADE,"D");

                     String[] data = {"a005","Nobody","A+"};
                     table.add( data );



                     System.out.println("------ search A+ -----------");
                     while( table.search(GRADE,"A+") )
                     {
                            System.out.print( table.get(CODE) + "/" );
                            System.out.print( table.get(NAME) + "/" );
                            System.out.println( table.get(GRADE) );
                     }

                     System.out.println("------ get second record -------");
                            System.out.print( table.get(CODE,1) + "/" );
                            System.out.print( table.get(NAME,1) + "/" );
                            System.out.println( table.get(GRADE,1) );


                     System.out.println("what is the grade of a004 = " + table.getValue("a004",GRADE) );


              }
              catch(Exception exp)
              {
                    System.out.println(exp);
              }

              try
              {
                     KTable table = new KTable(new FileInputStream("province.properties"));
                     System.out.println( "----- dump ----\r\n" + table );

              }
              catch(Exception exp)
              {
                    System.out.println(exp);
              }



       }

}
