/**
 * CMP layer for Account.
 * @author James Lee
 */
public abstract class AccountCMP
    extends xyz.domain.account.ejb.AccountEJB
    implements javax.ejb.EntityBean
{
    //~ Instance fields --------------------------------------------------------

    /* Value Objects BEGIN */
    private xyz.domain.account.model.AccountValue AccountValue = null;

    //~ Methods ----------------------------------------------------------------

    public void ejbLoad() {}

    public void ejbStore() {}

    public void ejbActivate() {}

    public void ejbPassivate()
    {
        AccountValue = null;
    }

    public void setEntityContext(javax.ejb.EntityContext ctx) {}

    public void unsetEntityContext() {}

    public void ejbRemove()
        throws javax.ejb.RemoveException {}

    public xyz.domain.account.model.AccountValue getAccountValue()
    {
        AccountValue = new xyz.domain.account.model.AccountValue();
        try
        {
            AccountValue.setAccountId(getAccountId());
            AccountValue.setAccountCode(getAccountCode());
            AccountValue.setFirstname(getFirstname());
            AccountValue.setLastname(getLastname());
            AccountValue.setTelephone(getTelephone());
            AccountValue.setLanguage(getLanguage());
            AccountValue.setStreet1(getStreet1());
            AccountValue.setStreet2(getStreet2());
            AccountValue.setCity(getCity());
            AccountValue.setProvince(getProvince());
            AccountValue.setPostalCode(getPostalCode());
            AccountValue.setCountry(getCountry());
        }
        catch (Exception e)
        {
            throw new javax.ejb.EJBException(e);
        }

        return AccountValue;
    }

    public void setAccountValue(xyz.domain.account.model.AccountValue valueHolder)
    {
        try
        {
            setAccountCode(valueHolder.getAccountCode());
            setFirstname(valueHolder.getFirstname());
            setLastname(valueHolder.getLastname());
            setTelephone(valueHolder.getTelephone());
            setLanguage(valueHolder.getLanguage());
            setStreet1(valueHolder.getStreet1());
            setStreet2(valueHolder.getStreet2());
            setCity(valueHolder.getCity());
            setProvince(valueHolder.getProvince());
            setPostalCode(valueHolder.getPostalCode());
            setCountry(valueHolder.getCountry());
        }
        catch (Exception e)
        {
            throw new javax.ejb.EJBException(e);
        }
    }

    /* Value Objects END */
    public abstract java.lang.Integer getAccountId();

    public abstract void setAccountId(java.lang.Integer accountId);

    public abstract java.lang.String getAccountCode();

    public abstract void setAccountCode(java.lang.String accountCode);

    public abstract java.lang.String getFirstname();

    public abstract void setFirstname(java.lang.String firstname);

    public abstract java.lang.String getLastname();

    public abstract void setLastname(java.lang.String lastname);

    public abstract java.lang.String getTelephone();

    public abstract void setTelephone(java.lang.String telephone);

    public abstract java.lang.String getLanguage();

    public abstract void setLanguage(java.lang.String language);

    public abstract java.lang.String getStreet1();

    public abstract void setStreet1(java.lang.String street1);

    public abstract java.lang.String getStreet2();

    public abstract void setStreet2(java.lang.String street2);

    public abstract java.lang.String getCity();

    public abstract void setCity(java.lang.String city);

    public abstract java.lang.String getProvince();

    public abstract void setProvince(java.lang.String province);

    public abstract java.lang.String getPostalCode();

    public abstract void setPostalCode(java.lang.String postalCode);

    public abstract java.lang.String getCountry();

    public abstract void setCountry(java.lang.String country);

}
