Is this really immutable? What do you think and see?

public class Period {

    private Date start;
    private Date end;

    public Period(Date start, Date end) {
        if (start==null || end==null) {
            throw new IllegalArgumentException("Start or end date cannot be null.");
        }
        this.start = start;
        this.end = end;
    }

    public Date getStart() {
        return this.start;
    }

    public Date getEnd() {
        return this.end;
    }

}
